import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';


const ItemListContainer = () => {
    const [item, setItem] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        const promesa = new Promise((resolve) => {
            setTimeout(() => {
                if (id === undefined) {
                    const fetchData = async () => {
                        try {
                            const range1Promise = fetch('https://dummyjson.com/products?range=0-9');
                            const range2Promise = fetch('https://dummyjson.com/products?range=61-70');
                            const [range1Response, range2Response] = await Promise.all([range1Promise, range2Promise]);
                            const range1Data = await range1Response.json();
                            const range2Data = await range2Response.json();
                            if (!Array.isArray(range1Data) || !Array.isArray(range2Data)) {
                                throw new Error('Invalid data format');
                            }
                            const mergedData = [...range1Data, ...range2Data];
                            setItem(mergedData);
                        } catch (error) {
                            console.error(error);
                        }
                    }
                    fetchData();
                }
                else {
                    resolve(fetch(`https://dummyjson.com/products/category/${id}`))
                }
                ;
            }, 300);
        });
        promesa.then((resolve) => resolve.json())
            .then((data) => {
                setItem(data);
                console.log(data);
            });
    }, [id]);
    console.log(id)

    return (
        <div>

        </div>
    );
};


export default ItemListContainer;
