import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';


const ItemListContainer = () => {
    const [item, setItem] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        if (id === undefined) {
            const categories = ["Smartphones", "Laptops", "Men's Watches", "Women's Watches"];
            const promises = categories.map(category => {
                return new Promise((resolve) => {
                    setTimeout(() => {
                        resolve(fetch(`https://dummyjson.com/products/category/${category}`))
                    }, 300);
                });
            });
            Promise.all(promises)
                .then(responses => Promise.all(responses.map(response => response.json())))
                .then(data => {
                    const products = data.reduce((accumulator, currentValue) => {
                        return accumulator.concat(currentValue);
                    }, []);
                    setItem(products);
                    console.log(products)
                });


        }
        else {
            const promesa = new Promise((resolve) => {
                setTimeout(() => {
                    resolve(fetch(`https://dummyjson.com/products/category/${id}`))
                }, 300);
            });
            promesa.then((resolve) => resolve.json())
                .then((data) => {
                    const products = data
                    setItem(products);
                    console.log(products);
                });
        }
    }, [id]);
    console.log(id)
    return (
        <div>
            <div>
                {products.map(item => (
                    <div key={item.id}>
                        <h2>{item.title}</h2>
                        <p>{item.description}</p>
                        <p>{item.price}</p>
                        <img src={item.image} alt={item.title} />
                    </div>
                ))}
            </div>
        </div>
    )
};


export default ItemListContainer;
