import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';


const ItemListContainer = () => {
    const [item, setItem] = useState([]);
    const { id } = useParams();

    useEffect(() => {
        const promesa = new Promise((resolve) => {
            setTimeout(() => {
                if (id === undefined) {
                    const fetchData = async () => {
                        try {
                            const range1Promise = fetch('https://dummyjson.com/products?limit=9');
                            const range2Promise = fetch('https://dummyjson.com/products?skip=61&limit=9');
                            const [range1Response, range2Response] = await Promise.all([range1Promise, range2Promise]);
                            const range1Data = [await range1Response.json()];
                            const range2Data = [await range2Response.json()];
                            const mergedData = range1Data.concat(range2Data);
                            const usefulData = mergedData.map(element => element.products);
                            setItem(usefulData);
                            console.log(usefulData);
                        } catch (error) {
                            console.error(error);
                        }
                    }
                    fetchData();
                }
                else {
                    resolve(fetch(`https://dummyjson.com/products/category/${id}`))
                    promesa.then((resolve) => resolve.json())
                        .then((data) => {
                            setItem([data]);
                        });
                }
                ;
            }, 300);
        });

    }, [id]);
    console.log(id)
    if (id === undefined) {
        return (
            <div>
                {item.map(arr => (
                    arr.map(i => (
                        <div key={i.id}>
                            <h2>{i.title}</h2>
                        </div>
                    ))
                ))}
            </div>
        );
    }
    else {
        console.log(item)
        return (
            <div>
                {item.map(arr => (
                    arr.map(i => (
                        <div key={i.id}>
                            <h2>{i.title}</h2>
                        </div>
                    ))
                ))}
            </div>
        );
    }

};


export default ItemListContainer;
